mapper.insert:
		insert into course(
      instructor_id, title, description,
      created_at, price, level, category_id, img, file_id
  ) VALUES (
      #{instructor_id}, #{title}, #{description},
      CURRENT_TIMESTAMP, #{price}, #{level}, #{category_id}, #{img}, #{file_id}
  )

mapper.select:
SELECT *
FROM course
WHERE category_id = #{categoryId}
   OR category_id IN (
       SELECT category_id
       FROM category
       WHERE parent_id = #{categoryId}
   )
ORDER BY instructor_id DESC
