mapper.select:
  		SELECT * FROM edudb.instructor
		join upload_file
		using (file_id)

  		SELECT count(*) FROM edudb.instructor
		join upload_file
		using (file_id)

SELECT *
FROM instructor
WHERE category_id = #{categoryId}
   OR category_id IN (
       SELECT category_id
       FROM category
       WHERE parent_id = #{categoryId}
   )
ORDER BY instructor_id DESC
